//
//  "watch" code for Mission05_OEM mission
//
void Watch_Mission05_OEM(void)
{
	CurrentMissionScope = KAS_SCOPE_MISSION;
	strcpy(CurrentMissionScopeName, "Mission05_OEM");
	if (!kasfVarValueGet("_VIFONCE053") && (1)) // "ifonce" #53
	{
	kasfVarCreateSet("_VIFONCE053", 1);
	(kasfLocationCard(10000, LSTRING_LocationCard[strCurLanguage]), 0);
	}
	if (!kasfVarValueGet("_VIFONCE054") && ((kasfShipsCount(kasAITeamShipsPtr("TuranicCarrier1")) < 1) && (kasfShipsCount(kasAITeamShipsPtr("TuranicCarrier2")) < 1))) // "ifonce" #54
	{
	kasfVarCreateSet("_VIFONCE054", 1);
	(kasfMusicStop(12), 0);
	(kasfVarCreateSet("G_ByeByeLove", 1), 0);
	(kasfObjectiveSet("DestroyEnemyCarriers", 1), 0);
	(kasfObjectiveCreate("Hyperspace", LSTRING_Hyperspace[strCurLanguage], LSTRING_Hyperspace[strCurLanguage]), 0);
	}
	if ((kasfDoesPlayerHave("MassDrive1Kt")) && (kasfVarValueGet("SetTechOnce1") == 0))
	{
	(kasfAllowPlayerToResearch("Chassis1"), 0);
	(kasfVarCreateSet("SetTechOnce1", 1), 0);
	}
	if ((kasfDoesPlayerHave("MassDrive10Kt")) && (kasfVarValueGet("SetTechOnce2") == 0))
	{
	(kasfAllowPlayerToResearch("Chassis2"), 0);
	(kasfVarCreateSet("SetTechOnce2", 1), 0);
	}
	if ((kasfDoesPlayerHave("MassDrive100Kt")) && (kasfVarValueGet("SetCapitalChassis") == 0))
	{
	(kasfAllowPlayerToResearch("Chassis3"), 0);
	(kasfVarValueSet("SetCapitalChassis", 1), 0);
	}
	if ((kasfDoesPlayerHave("Chassis2")) && (kasfVarValueGet("SetMediumGuns") == 0))
	{
	(kasfAllowPlayerToResearch("MediumGuns"), 0);
	(kasfVarCreateSet("SetMediumGuns", 1), 0);
	}
	if (!kasfVarValueGet("_VIFONCE055") && (1)) // "ifonce" #55
	{
	kasfVarCreateSet("_VIFONCE055", 1);
	(kasfTimerCreateSetStart("G_PlayIonOrPlasmaEvent", 20), 0);
	}
	if (!kasfVarValueGet("_VIFONCE056") && (kasfTimerExpiredDestroy("G_PlayIonOrPlasmaEvent"))) // "ifonce" #56
	{
	kasfVarCreateSet("_VIFONCE056", 1);
	if ((kasfCanPlayerResearch("IonWeapons")) || (kasfDoesPlayerHave("IonWeapons")))
	{
	(kasfVarCreateSet("G_WeGetPlaz", 1), 0);
	(kasfAllowPlayerToResearch("PlasmaWeapons"), 0);
	}
	else if ((kasfCanPlayerResearch("PlasmaWeapons")) || (kasfDoesPlayerHave("PlasmaWeapons")))
	{
	(kasfVarCreateSet("G_WeGetIonz", 1), 0);
	(kasfAllowPlayerToResearch("IonWeapons"), 0);
	}
	}
	
}


