//
//  "watch" code for Mission13 mission
//
void Watch_Mission13(void)
{
	CurrentMissionScope = KAS_SCOPE_MISSION;
	strcpy(CurrentMissionScopeName, "Mission13");
	if (!kasfVarValueGet("_VIFONCE020") && (kasfRenderedShips(kasGrowSelectionPtr("GoneShips"), 3))) // "ifonce" #20
	{
	kasfVarCreateSet("_VIFONCE020", 1);
	(kasfMsgSendAll("FoundShip"), 0);
	}
	if (!kasfVarValueGet("_VIFONCE021") && ((kasfShipsCount(kasAITeamShipsPtr("JunkyardHQ")) < 1) && (kasfObjectiveGet("EstablishCommunicationsLink") == 0))) // "ifonce" #21
	{
	kasfVarCreateSet("_VIFONCE021", 1);
	(kasfMissionFailed(), 0);
	}
	if (!kasfVarValueGet("_VIFONCE022") && (kasfShipsCount(kasAITeamShipsPtr("Defector")) < 1)) // "ifonce" #22
	{
	kasfVarCreateSet("_VIFONCE022", 1);
	(kasfMissionFailed(), 0);
	}
	if (kasfVarValueGet("G_GunTimer") == 0)
	{
	(kasfVarValueSet("G_GunTimer", 1), 0);
	}
	else
	{
	(kasfVarValueSet("G_GunTimer", 0), 0);
	}
	if (!kasfVarValueGet("_VIFONCE023") && (kasfTimerExpiredDestroy("BigGunTimer"))) // "ifonce" #23
	{
	kasfVarCreateSet("_VIFONCE023", 1);
	(kasfAllowPlayerToResearch("HeavyGuns"), 0);
	(kasfMsgSendAll("BigGuns"), 0);
	(kasfTimerCreateSetStart("CloakingTimer", (kasfRandom(90, 240))), 0);
	}
	if (!kasfVarValueGet("_VIFONCE024") && (kasfTimerExpiredDestroy("CloakingTimer"))) // "ifonce" #24
	{
	kasfVarCreateSet("_VIFONCE024", 1);
	(kasfAllowPlayerToResearch("CloakGeneratorTech"), 0);
	(kasfMsgSendAll("CloakingTech"), 0);
	}
	if (!kasfVarValueGet("_VIFONCE025") && (kasfDoesPlayerHave("MassDrive10Kt"))) // "ifonce" #25
	{
	kasfVarCreateSet("_VIFONCE025", 1);
	(kasfAllowPlayerToResearch("Chassis2"), 0);
	}
	if ((kasfDoesPlayerHave("Chassis2")) && (kasfVarValueGet("SetMediumGuns") == 0))
	{
	(kasfAllowPlayerToResearch("MediumGuns"), 0);
	(kasfVarCreateSet("SetMediumGuns", 1), 0);
	}
	if (!kasfVarValueGet("_VIFONCE026") && (kasfDoesPlayerHave("Chassis1"))) // "ifonce" #26
	{
	kasfVarCreateSet("_VIFONCE026", 1);
	(kasfAllowPlayerToResearch("MassDrive10Kt"), 0);
	}
	
}


